import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Row.css";
function Row({ title, fetchUrl }) {
  const [newsTitle, setNewsTitle] = useState([]);
  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(fetchUrl);

      setNewsTitle(request.data.articles);
      return request;
    }
    function checkBoxes() {
      const trigger = (window.innerHeight / 5) * 4;
      boxes.forEach((box) => {
        const boxTop = box.getBoundingClientRect().top;
        if (boxTop < trigger) {
          box.classList.add("show");
        } else {
          box.classList.remove("show");
        }
      });
    }
    const boxes = document.querySelectorAll(".row-box");
    window.addEventListener("scroll", checkBoxes);

    fetchData();
  }, [fetchUrl]);
  console.table(newsTitle);
  function truncateString(str, num) {
    if (str.length > num) {
      return str.slice(0, num) + "...";
    } else {
      return str;
    }
  }

  return (
    <div className="row-body">
      <h2 id={title}>{title}</h2>
      <div className="row-container">
        {newsTitle.map((mov) => (
          <div className="row-box">
            <img
              className="row-images"
              src={mov.urlToImage}
              alt={mov.source.name}
            />
            <div class="" role="alert">
              <h2 className="row-title">{mov.title}</h2>
            </div>

            <a href={mov.url ? mov.url : ""}>
              <p class="row-content">
                {mov.content
                  ? truncateString(mov.content, 500)
                  : "Tap to know more"}
              </p>
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Row;

