import React, { useEffect } from "react";
import "./Container.css";
import { Link } from "react-router-dom";

function Container() {
  useEffect(() => {
    const loadText = document.querySelector(".loading-text");

    const panels = document.querySelectorAll(".pannel");
    function removeActive() {
      panels.forEach((mov) => mov.classList.remove("active"));
    }
    panels.forEach((mov) =>
      mov.addEventListener("click", () => {
        removeActive();
        mov.classList.add("active");
      })
    );
  }, []);

  return (
    <>
      <div className="loading-text">0%</div>
      <div className="container-body">
        <div className="container">
          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://wallpapercave.com/wp/wp4729462.jpg')",
            }}
          >
            <Link to="/news">
              <h3>Explore Live News Updates</h3>
            </Link>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://images.hdqwalls.com/wallpapers/the-joker-joaquin-phoenix-dark-4k-z9.jpg')",
            }}
          >
            <h3>Want to know about the latest movies?</h3>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://besthqwallpapers.com/Uploads/29-11-2019/113521/thumb2-dreams-dont-work-unless-you-do-john-c-maxwell-4k-black-background-motivation-quotes.jpg')",
            }}
          >
            <h3>These Quotes will surely lift your spirits!</h3>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://i.pinimg.com/originals/ab/85/8f/ab858fbfd2c5aa35bf96350498d695ca.jpg')",
            }}
          >
            <h3>Bored? Play this game with a friend!</h3>
          </div>
          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://cdn.pixabay.com/photo/2018/01/14/23/12/nature-3082832__480.jpg')",
            }}
          >
            <h3>Find intersting images </h3>
          </div>
        </div>
      </div>
    </>
  );
}
