import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Row.css";
function Row({ title, fetchUrl }) {
  const [newsTitle, setNewsTitle] = useState([]);
  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(fetchUrl);

      setNewsTitle(request.data.articles);
      return request;
    }

    const boxes = document.querySelectorAll(".row-box");
    window.addEventListener("scroll", checkBoxes);
    checkBoxes();
    function checkBoxes() {
      const trigger = (window.innerHeight / 5) * 4;
      boxes.forEach((box) => {
        const boxTop = box.getBoundingClientRect().top;
        if (boxTop < trigger) {
          box.classList.add("show");
        } else {
          box.classList.remove("show");
        }
      });
    }

    fetchData();
  }, [fetchUrl]);
  console.table(newsTitle);
  // function truncateString(str, num) {
  //   if (str.length > num) {
  //     return str.slice(0, num) + "...";
  //   } else {
  //     return str;
  //   }
  // }

  return (
    <div className="">
      <h2 id={title}>{title}</h2>
      <div className="container">
        {newsTitle.map((mov) => (
          <div className="new_box" style={{}}>
            <img className="" src={mov.urlToImage} alt={mov.source.name} />
            <div className="">
              <h5 className="">{mov.title}</h5>
              <p className="">
                {" "}
                {mov.content ? mov.content : "Tap to know more"}
              </p>
              <a href={mov.url ? mov.url : ""} className="btn btn-primary">
                Go to source
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Row;

