import React, { useEffect } from "react";
import "./Container.css";
import { Link } from "react-router-dom";
import logo from "./Movie/Nordt.png";

function Container() {
  useEffect(() => {
    /*                FOR BLUR                         */
    const loadText = document.querySelector(".loading-text");
    const bg = document.querySelector(".container-body");
    let load = 0;
    let int = setInterval(blur, 20);
    function blur() {
      load++;
      if (load > 99) {
        clearInterval(int);
      }
      loadText.innerHTML = `${load}%`;
      loadText.style.opacity = scale(load, 0, 100, 1, 0);
      bg.style.filter = `blur(${scale(load, 0, 100, 100, 0)}px)`;
    }
    /*                Function to MAP a value to another                      */
    function scale(number, inMin, inMax, outMin, outMax) {
      return ((number - inMin) * (outMax - outMin)) / (inMax - inMin) + outMin;
    }

    /*                FOR BLUR                         */

    /*                FOR PANEL STRECTH                         */
    const panels = document.querySelectorAll(".pannel");
    function removeActive() {
      panels.forEach((mov) => mov.classList.remove("active"));
    }
    panels.forEach((mov) =>
      mov.addEventListener("click", () => {
        removeActive();
        mov.classList.add("active");
      })
    );
  }, []);

  return (
    <>
      <div className="loading-text">0%</div>

      <div className="container-body">
        <Link to="/">
          <img className="logo" src={logo} alt="logo" />
        </Link>
        <div className="container">
          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://wallpapercave.com/wp/wp4729462.jpg')",
            }}
          >
            <Link to="/news">
              <h3>Live News</h3>
            </Link>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://images.hdqwalls.com/wallpapers/the-joker-joaquin-phoenix-dark-4k-z9.jpg')",
            }}
          >
            <Link to="/movie">
              <h3>Movies</h3>
            </Link>
          </div>
          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://i.pinimg.com/originals/ab/85/8f/ab858fbfd2c5aa35bf96350498d695ca.jpg')",
            }}
          >
            <Link to="/game">
              <h3>Game</h3>
            </Link>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://besthqwallpapers.com/Uploads/29-11-2019/113521/thumb2-dreams-dont-work-unless-you-do-john-c-maxwell-4k-black-background-motivation-quotes.jpg')",
            }}
          >
            <h3>These Quotes will surely lift your spirits!</h3>
          </div>

          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://images.unsplash.com/photo-1542127306-0c218c711733?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZGFyayUyMHdlYXRoZXJ8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80')",
            }}
          >
            <Link to="/weather">
              <h3>Weather</h3>
            </Link>
          </div>
          <div
            className="pannel "
            style={{
              backgroundImage:
                "url('https://wallpapercave.com/wp/wp3172900.jpg')",
            }}
          >
            <h3>
              <a href="https://map-app-27862.web.app/">Personalised Map</a>
            </h3>
          </div>
        </div>
      </div>
    </>
  );
}
